mapper.select:
	    SELECT GOAL_NO
	    from CHALLENGE_ROOM
   	    where Chall_NO = #{challNo}

SELECT CHALL_STARTDATE
FROM CHALLENGE_ROOM
WHERE Chall_NO = #{challNo}

    	SELECT CP.id
	    from CHALLENGE_ROOM CR join CHALLENGE_PARTICIPANTS CP on CR.CHALL_NO = CP.CHALL_NO
	    where CR.Chall_NO = #{challNo}

		SELECT CR.GOAL_NO, CP.CHALL_IMPLEMENTATION_RATE
    	FROM CHALLENGE_ROOM CR join CHALLENGE_PARTICIPANTS CP on CR.CHALL_NO = CP.CHALL_NO
    	WHERE CP.id =#{id}
